package com.testing;

public class swap3rd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=20;
int c=0;

System.out.println("Before Swapping");
System.out.println(a);
System.out.println(b);

c=a;// c=10
a=b;// a=20
b=c; // b=10

System.out.println("After Swapping");
System.out.println(a);
System.out.println(b);

	}

}
